module load bcl2fastq2FLOWCELL_DIR={path to raw data}SAMPLE_SHEET_PATH={path to samplesheet.csv}OUTPUT_DIR={path for fastq outut dir}INTEROP_DIR={path for stats outut dir}bcl2fastq --use-bases-mask=Y101, I8, n8Y16, Y101 \          --create-fastq-for-index-reads \          --minimum-trimmed-read-length=8 \          --mask-short-adapter-reads=8 \          --ignore-missing-positions \          --ignore-missing-controls \          --ignore-missing-filter \          --ignore-missing-bcls \          -r 6 -w 6 \          --tiles s_[1-2] \ # lane number           -R ${FLOWCELL_DIR} \          --output-dir=${OUTPUT_DIR} \          --interop-dir=${INTEROP_DIR} \          --sample-sheet=${SAMPLE_SHEET_PATH}